import { useEffect  } from "react";

const HeaderEvent = () => {
    const handleMouseover = (e) => {
        const target = e.currentTarget;
        const depth2 = target.querySelector('.depth2');

        if (depth2) {
            depth2.style.display = 'block';
        }
    };

    useEffect(() => {
        const navLinks = document.querySelectorAll("#gnb > li");

        navLinks.forEach((link) => {
            link.addEventListener("click", handleMouseover);
        });

        // Clean-up 함수 등록
        return () => {
            navLinks.forEach((link) => {
                link.removeEventListener("click", handleMouseover);
            });
        };
    }, []);

    return null;
};

export default HeaderEvent;
