import { useEffect } from "react";

const HeaderEvent = () => {
    const mouseenter = (e) => {
        const target = e.currentTarget;
        const depth2 = target.querySelector('.depth2');

        if(depth2) {
            depth2.classList.add('on');
        }
        
    };

    const mouseleave = (e) => {
        const target = e.currentTarget;
        const depth2 = target.querySelector('.depth2');

        if(depth2) {
            depth2.classList.remove('on');
        }
        
    };

    useEffect(() => {
        const navLinks = document.querySelectorAll("#gnb > li");

        navLinks.forEach((link) => {
            link.addEventListener("mouseenter", mouseenter);
            link.addEventListener("mouseleave", mouseleave);
        });

        // Clean-up 함수 등록
        return () => {
            navLinks.forEach((link) => {
                link.removeEventListener("mouseenter", mouseenter);
                link.removeEventListener("mouseleave", mouseleave);
            });
        };
    }, []);

    return null;
};

export default HeaderEvent;
