import styled from 'styled-components';
import mq from '../../MediaQuery';

const HeaderContainer = styled.header`
    box-shadow: 0 0px 15px rgba(0 0 0 / 15%);

    .header-wrap {
        position: relative;
        max-width: 1520px;
        margin: 0 auto;

        ${mq.maxWidth('desktopM')`
            margin : 0 20px;
        `};
    }

    h1 {
        position: absolute;
        top: 50%;
        left: 0;
        transform: translateY(-50%);

        a {
            display: block;
            width: 150px;
            height: 50px;
            background: no-repeat url('/images/layout/logo.png') center;
        }
    }

    #gnb {
        display: flex;
        gap: 20px;
        justify-content: center;
        align-items: center;

        > li {
            position: relative;

            &:hover > a {
                position: relative;
                color: rgb(49, 130, 246);

                &::before {
                    content: '';
                    position: absolute;
                    bottom: 0;
                    left: 50%;
                    width: 8px;
                    height: 8px;
                    border-radius: 50%;
                    background-color: rgb(49, 130, 246);
                    transform: translateX(-50%);
                }
            }

            > a {
                display: block;
                padding: 30px 15px;
                font-weight: 600;
                font-size: 1.25rem;
            }
        }

        .depth2 {
            position: absolute;
            top: 100%;
            left: 0;

