            &:first-child::before {
                display: none;
            }

            a {
                font-weight: 500;
                font-size: 0.875rem;

                &:hover {
                    color: rgb(49, 130, 246);
                    font-weight: 600;
                }
            }
        }
    }
`;

export default HeaderContainer;
