import React, { memo } from "react";
import styled from "styled-components";
import mq from "../MediaQuery";
import { NavLink } from "react-router-dom";

const HeaderContainer = styled.header`
    box-shadow: 0 3px 10px rgba(0 0 0 / 30%);

    .header-wrap {
        position: relative;
        max-width: 1520px;
        margin: 0 auto;
    }

    h1 {
        position: absolute;
        top: 50%;
        left: 0;
        transform: translateY(-50%);

        a {
            display: block;
            width: 100px;
            height: 40px;
            background-color: orange;
        }
    }

    #gnb {
        display: flex;
        justify-content: center;
        align-items: center;

        > li {
            position: relative;
        }

        > li > a {
            display: block;
            margin: 20px 0;
            padding: 10px 15px;
        }

        .depth2 {
            position: absolute;
            top: 100%;
            left: 0;
        }
    }

    .utils {
        position: absolute;
        top: 50%;
        right: 0;
        transform: translateY(-50%);

        display: flex;
    }
`;

const Header = memo(() => {
    return (
        <HeaderContainer>
            <div className="header-wrap">
                <h1>
                    <NavLink to="/">
                        <span className="hide">로고</span>
                    </NavLink>
                </h1>

                <ul id="gnb">
                    <li>
                        <NavLink to="/about">홍길동</NavLink>
                        <ul className="depth2">
                            <li>
                                <NavLink to="/about">자기소개</NavLink>
                            </li>
                            <li>
                                <NavLink to="/about/history">연혁</NavLink>
                            </li>
                        </ul>
                    </li>
                    <li>
                        <NavLink to="/product">상품</NavLink>
                    </li>
                    <li>
                        <NavLink to="/notice">공지사항</NavLink>
                    </li>
                    <li>
                        <NavLink to="/board">게시판</NavLink>
                    </li>
                </ul>

                <ul className="utils">
                    <li>
                        <NavLink to="/sign">회원가입</NavLink>
                    </li>
                    <li>
                        <NavLink to="/login">로그인</NavLink>
                    </li>
                </ul>
            </div>
        </HeaderContainer>
    );
});

export default Header;
