import { useEffect, useState } from "react";

const HeaderEvent = () => {
    const [screenSize, setScreenSize] = useState(window.innerWidth);
    const [isMobile, setIsMobile] = useState(window.matchMedia("(max-width: 1024px)").matches);

    const updateScreenSize = () => {
        setScreenSize(window.innerWidth);
        setIsMobile(window.matchMedia("(max-width: 1024px)").matches);
    };

    useEffect(() => {

        updateScreenSize();

        window.addEventListener('resize', updateScreenSize);

        return () => {
            window.removeEventListener('resize', updateScreenSize);
        };
    }, []);

    useEffect(() => {
        console.log(screenSize);
        console.log(isMobile);
    }, [screenSize, isMobile]);

    const mouseEvent = (e, action) => {
        const target = e.currentTarget;
        const depth2 = target.querySelector('.depth2');

        if(screenSize > 1024 && isMobile === false && depth2) {
            // PC일때
            depth2.classList.toggle('on', action === 'add');
            depth2.style.maxHeight = action === 'add' ? `${depth2.scrollHeight}px` : '0';
        }
    };

    const clickEvent = (e, action) => {
        const target = e.currentTarget;
        const depth2 = target.querySelector('.depth2');

        if(1024 >= screenSize  && isMobile === true && depth2) {
            // Mobile일때
            depth2.classList.toggle('on', action === 'add');
        }
    };
    
    useEffect(() => {
        const navLinks = document.querySelectorAll("#gnb > li");

        navLinks.forEach((link) => {
            link.addEventListener("mouseenter", (e) => mouseEvent(e, 'add'));
            link.addEventListener("mouseleave", (e) => mouseEvent(e, 'remove'));

            link.addEventListener("click", (e) => clickEvent(e, 'add'));
            link.addEventListener("click", (e) => clickEvent(e, 'remove'));
        });

        // Clean-up 함수 등록
        return () => {
            navLinks.forEach((link) => {
                link.removeEventListener("mouseenter", (e) => mouseEvent(e, 'add'));
                link.removeEventListener("mouseleave", (e) => mouseEvent(e, 'remove'));

                link.removeEventListener("click", (e) => clickEvent(e, 'add'));
                link.removeEventListener("click", (e) => clickEvent(e, 'remove'));
            });
        };
    }, []);

    return null;
};

export default HeaderEvent;
